spine.BoneData = function (name, parent) {
  // Create a new BoneData object with the given name and parent.

  this.name = name; // The name of the bone.
  this.parent = parent; // The parent bone or null if this is the root bone.
};

spine.BoneData.prototype = {
  length: 0, // The length of the bone in pixels.
  x: 0, // The x-coordinate of the bone's local position.
  y: 0, // The y-coordinate of the bone's local position.
  rotation: 0, // The rotation of the bone in degrees.
  scaleX: 1, // The scale factor along the x-axis.
  scaleY: 1, // The scale factor along the y-axis.
  inheritScale: true, // Whether the bone inherits its parent's scale.
  inheritRotation: true, // Whether the bone inherits its parent's rotation.
};
