/**
 * Attempts to resolve `input` URL/path relative to `base`.
 */
import url from 'url';

export default function resolve(input: string, base?: string): string | never {
  if (!base) {
    throw new Error('`base` parameter cannot be undefined');
  }

  if (typeof base !== 'string') {
    throw new Error('`base` parameter must be a string');
  }

  return url.resolve(base, input);
}
