export function cloneNode(n, deep = false) {
  if (deep && typeof n === 'object' && n !== null) {
    if (Array.isArray(n)) {
      return n.map(cloneNode.bind(null, false));
    } else {
      const clonedObj = {};
      for (const key in n) {
        if (n.hasOwnProperty(key)) {
          clonedObj[key] = cloneNode(n[key], true);
        }
      }
      return clonedObj;
    }
  } else {
    return Object.assign({}, n);
  }
}
